import React, { useState } from "react";
import './consultationrequest.css'

const ConsultationRequest = () => {
  const [type, setType] = useState("");
  const [message, setMessage] = useState("");
  const [firstName, setFirstName] = useState("");
  const [surname, setSurname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [checked, setChecked] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({
      type,
      message,
      firstName,
      surname,
      phone,
      email,
      checked,
    });
  };

  return (
    <div className="container">
      <div className="form-holder border m-auto rounded px-5">
        <p>What type of consultation would you like?</p>
        <form onSubmit={handleSubmit}>
          <div className="radios">
            <label className="">
              <input
                type="radio"
                value="phone"
                checked={type === "phone"}
                onChange={() => setType("phone")}
              />
              Phone call
            </label>
            <label>
              <input
                type="radio"
                value="face-to-face"
                checked={type === "face-to-face"}
                onChange={() => setType("face-to-face")}
              />
              Face to face
            </label>
          </div>

          <div>
            <label htmlFor="message">What would you like to discuss?</label>
            <textarea
              id="message"
              placeholder="Please enter your message here"
              value={message}
              onChange={(event) => setMessage(event.target.value)}
            />
          </div>

          <div>
            <label htmlFor="firstName">First name</label>
            <input
              id="firstName"
              type="text"
              placeholder="Enter your first name"
              value={firstName}
              onChange={(event) => setFirstName(event.target.value)}
            />
          </div>

          <div>
            <label htmlFor="surname">Surname</label>
            <input
              id="surname"
              type="text"
              placeholder="Enter your surname"
              value={surname}
              onChange={(event) => setSurname(event.target.value)}
            />
          </div>

          <div>
            <label htmlFor="phone">Phone number</label>
            <input
              id="phone"
              type="text"
              placeholder="Enter your phone number"
              value={phone}
              onChange={(event) => setPhone(event.target.value)}
            />
          </div>

          <div>
            <label htmlFor="email">Email address</label>
            <input
              id="email"
              type="email"
              placeholder="Enter your email address"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            />
          </div>

          <div>
            <label>
              <input
                type="checkbox"
                checked={checked}
                onChange={() => setChecked(!checked)}
              />
              I am not a robot
            </label>
          </div>

          <button
            type="submit"
            className="btn btn-primary mb-3"
            disabled={!checked}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default ConsultationRequest;
