import { Breadcrumb } from "react-bootstrap";
import { useLocation } from "react-router-dom";

function Crumbs() {
  const location = useLocation();

  // Get the path segments of the current location
  const pathSegments = location.pathname
    .split("/")
    .filter((segment) => segment !== "");

  // Map the path segments to Breadcrumb.Item components
  const breadcrumbItems = pathSegments.map((segment, index) => {
    const href = `/${pathSegments.slice(0, index + 1).join("/")}`;
    return (
      <Breadcrumb.Item key={href} href={href}>
        {segment}
      </Breadcrumb.Item>
    );
  });

  // Add the active page as the last item in the breadcrumb
  const activePage = breadcrumbItems.pop();
  breadcrumbItems.push(
    <Breadcrumb.Item key={activePage.props.href} active>
      {activePage.props.children}
    </Breadcrumb.Item>
  );

  return <Breadcrumb>{breadcrumbItems}</Breadcrumb>;
}

export default Crumbs;
