import React from "react";

function Navigation() {


    const [showMenu, setShowMenu] = useState(false);

  return (
    <div>
      <nav className="navbar">
        <div className="container">
          <label
            className="navbar-toggle"
            id="js-navbar-toggle"
            for="chkToggle"
          >
            <i className="fa fa-bars"></i>
          </label>
          <a href="#" className="logo">
            logo
          </a>
          <input type="checkbox" id="chkToggle"></input>

          <ul className="main-nav main-menu" id="js-menu">
            <li>Home</li>
            <li>
              About
              <ul className="submenu rounded">
                <li>Who we are</li>
                <li>How we Work</li>
                <li>Our Partners</li>
              </ul>
            </li>
            <li>
              Financial Planning
              <ul className="submenu rounded">
                <li>Financial Planning Life Stages</li>
                <li>Business Owners</li>
              </ul>
            </li>
            <li>Investment Planning</li>
            <li>
              Insurance
              <ul className="submenu rounded">
                <li>Personal</li>
                <li>Business</li>
                <li>Specialty</li>
              </ul>
            </li>
            <li>Blog</li>
            <li>Contact Us</li>
          </ul>
        </div>
        <button
          className="btn btn-primary menu-btn"
          onClick={() => setShowMenu(!showMenu)}
        >
          Menu
        </button>
      </nav>
      <ul
        className="mobile-menu rounded"
        style={{
          display: showMenu ? "block" : "none",
        }}
      >
        <li>Home</li>
        <li>About</li>
        <li>Financial Planning</li>
        <li>Investment Planning</li>
        <li>Insurance</li>
        <li>Blog</li>
        <li>Contact Us</li>
      </ul>
    </div>
  );
}

export default Navigation;
